INSERT INTO `good` (`id`, `name`, `farmer_id`, `category_id`, `price`, `quantity`, `measure_id`, `brief`, `description`, `is_visible`, `img`) VALUES
(337, 'Грецкий орех неочищенный (н/о)', 11, 16, 485.00, 1, 3, 'Грецкий орех неочищенный (н/о), Россия.', 'Родиной грецкого ореха считается Средняя Азия, но с потеплением климата и выведением новых сортов территория его произрастания распространилась шире, хотя в промышленных масштабах он выращивается преимущественно в южных районах.\r\nОчень широкое применение грецких орехов в кавказской кухне. Помимо сладостей, это и блюда из мяса, птицы и рыбы, а также традиционные соусы.', 0, NULL),
(338, 'Грецкий орех очищенный, половинки', 11, 16, 560.00, 1, 3, 'Грецкий орех очищенный, половинки, Россия', 'Как сами ядра, так и другие части грецкого ореха (от кожуры до семенных перегородок) широко используются в народной медицине, хотя основная область его применения – употребление в пищу, как в самостоятельном виде (снек), так и в составе различных блюд, прежде всего кондитерских изделий.', 0, NULL),
(339, 'Грецкий орех очищенный (о/ч) половинки премиум', 11, 16, 1150.00, 1, 3, 'Грецкий орех очищенный (о/ч) половинки премиум, Россия', 'Ядро грецкого ореха покрыто тонкой оболочкой от светло-желтого до темно-коричневого цвета, при этом светлые ядра относятся к высшему сорту, а с темной оболочкой – к более низкому. У спелого грецкого ядро ореха имеет влажный блеск. При необходимости оболочка легко снимается после погружения на несколько часов в подсоленную воду, а затем ядра промываются и высушиваются.', 0, NULL),
(340, 'Грецкий орех очищенный (о/ч) половинки экстра', 11, 16, 750.00, 1, 3, 'Грецкий орех очищенный (о/ч) половинки экстра, Россия', 'Кулинары, как правило, вводят грецкий орех в дробленом или растертом виде, а также в виде половинок ядер. Используют для изготовления начинок, посыпок, украшений, добавляют для прослойки в тортах, пирожных, вафлях и др.', 0, NULL),
(341, 'Грецкий орех очищенный (о/ч) четвертинки', 11, 16, 500.00, 1, 3, 'Грецкий орех очищенный (о/ч) четвертинки, Россия', 'Грецкие орехи не обжаривают, так как содержащийся в них жир легко прогорает и приобретает неприятный привкус. Поэтому, в отличие от других видов орехов, в кондитерском производстве они используются реже: наиболее широкое применение – в изготовлении восточных сладостей и, сравнительно мало – в производстве конфет и карамели.', 0, NULL),
(342, 'Грецкий орех очищенный, четвертинки, высший сорт', 11, 16, 700.00, 1, 3, 'Грецкий орех очищенный, четвертинки, высший сорт, Россия', 'В 100 г. грецких орехов содержится:\r\nвода – 4,4 г., белки – 15,5 г., жиры – 65,0 г., углеводы – 10,1 г.,\r\nпищевые волокна (клетчатка) – 1,5 г.\r\n\r\nВитамины:\r\nвитамин А (бета-каротин) – 0,05 мг., витамин В1 (тиамин) – 0,4 мг.,\r\nвитамин В2 (рибофлавин) – 0,1 мг., витамин В3 или витамин РР (ниацин) – 1,0 мг.,\r\nвитамин В9 (фолиевая кислота) – 77,0 мкг., витамин С (аскорбиновая кислота) – 3,0 мг., витамин Е (токоферол) – 23,0 мг.', 0, NULL),
(343, 'Абрикосовая косточка', 11, 16, 175.00, 1, 3, 'Абрикосовая косточка', 'В кулинарии используются ядра абрикосовых косточек, для чего необходимо расколоть твердую скорлупу, а затем еще и удалить покрывающую их тонкую кожуру, которая дает горечь. С сухими абрикосовыми косточками этот процесс может быть весьма трудоемким, но если залить их кипятком на несколько минут, кожура размокнет и легко снимется.\r\nЯдра абрикосовой косточки очень полезны. У некоторых сортов абрикосов косточка и соответственно ядро очень крупные, в них содержится порядка 70% сладковатого на вкус ценного пищевого масла и до 20% белка. Считается, что употребление абрикосовых косточек и абрикосового масла в пищу способствует улучшению здоровья и помогает бороться с онкозаболеваниями.\r\n\r\nВ 100 г абрикосовой косточки содержится:\r\nвода – 5,4 г., белки – 14,0 г., жиры – 27,7 г., углеводы – 56,3 г.\r\nВитамины: витамин В3 или витамин РР (ниацин) – 4,2 мг.', 0, NULL),
(344, 'Арахис бланшированный', 11, 16, 120.00, 1, 3, 'Арахис бланшированный, Аргентина', 'Благодаря своей невысокой цене и отличным вкусовым качествам арахис является одним из наиболее популярных и часто используемых орехов. Основная область применения –- кондитерская и пищевая промышленность.\r\nПрекрасно проявляет себя в любой разновидности теста, поэтому большой популярностью пользуется выпечка с арахисом – печенье, кексы, пряники, вафли, рулеты, пироги и др. Отлично сочетается с молочными продуктами, морепродуктами и мясом.\r\nВ 100 г арахиса содержится: вода – 6,5 г., белки – 25,8 г., жиры – 49,2 г.,\r\nуглеводы – 9,9 г., пищевые волокна (клетчатка) – 8,5 г.\r\nВитамины: витамин В1 (тиамин) – 0,6 мг., витамин В2 (рибофлавин) – 0,1 мг.,\r\nвитамин В3 или витамин РР (ниацин) – 12,1 мг., витамин В4 (холин) – 52,5 мг.,\r\nвитамин В5 (пантотеновая кислота) – 1,8 мг., витамин В6 (пиридоксин) – 0,4 мг.,\r\nвитамин В9 (фолиевая кислота) – 250,0 мкг., витамин E (токоферол) – 8,3 мг.', 1, 'dzF-gkX87zbND_3wcmiC.jpg'),
(345, 'Арахис в кокосовом соке', 11, 16, 165.00, 1, 3, 'Арахис в кокосовом соке, Вьетнам', 'Арахис в кокосовом соке – очень калорийный и сытный продукт. Зачастую используется для небольших перекусов. Прекрасное дополнение к горячему чаю и кофе.\r\nВ 100 г арахиса в кокосовом соке содержится: белки – 17,4 г., жиры – 32,0 г., \r\nуглеводы – 35,7 г. Энергетическая ценность 100 г арахиса в кокосовом соке – около 500,7 кКал / 2.096 кДж.', 0, NULL),
(346, 'Арахис жареный, соленый', 11, 16, 135.00, 1, 3, 'Арахис жареный, соленый, Россия', 'Арахис жареный – очень калорийный и сытный продукт. Зачастую используется для небольших перекусов. Помимо этого, может выступать как начинка или украшение для выпечки и кондитерских изделий. Также прекрасно дополняет слабоалкогольные напитки.\r\nВ 100 г обжаренного арахиса содержится: вода – 1,6 г., белки – 23,7 г., жиры – 49,7 г., углеводы – 13,5 г., пищевые волокна (клетчатка) – 8,0 г.\r\nВитамины: витамин В1 (тиамин) – 0,4 мг., витамин В2 (рибофлавин) – 0,1 мг.,\r\nвитамин В3 или витамин РР (ниацин) – 13,5 мг, витамин В4 (холин) – 55,3 мг.,\r\nвитамин В5 (пантотеновая кислота) – 1,4 мг., витамин В6 (пиридоксин) – 0,3 мг.,\r\nвитамин В9 (фолиевая кислота) – 145,0 мкг., витамин E (токоферол) – 6,9 мг.\r\nЭнергетическая ценность 100 г обжаренного арахиса – около 585,0 кКал / 2.448 кДж.', 0, NULL),
(347, 'Арахис жареный', 11, 16, 135.00, 1, 3, 'Арахис жареный, Россия', 'Арахис жареный – очень калорийный и сытный продукт. Зачастую используется для небольших перекусов. Помимо этого, может выступать как начинка или украшение для выпечки и кондитерских изделий. Также прекрасно дополняет слабоалкогольные напитки.', 0, NULL),
(348, 'Арахис неочищенный в скорлупе, Бразилия', 11, 16, 140.00, 1, 3, 'Арахис неочищенный в скорлупе, Китай, Аргентина, Индия, Бразилия', 'Являясь одним из самых распространенных и популярных орехов, арахис в действительности относится к семейству бобовых растений, правда, с одной уникальной особенностью: в отличие от своих собратьев, он плодоносит под землей, за что его часто называют "земляным орехом".\r\nПри соблюдении необходимых условий – сухое прохладное место, герметичность упаковки – неочищенный арахис может храниться многие месяцы не теряя своих качеств и питательной ценности.\r\nВ 100 г ядер арахиса содержится: вода – 6,5 г., белки – 25,8 г., жиры – 49,2 г.,\r\nуглеводы – 9,9 г., пищевые волокна (клетчатка) – 8,5 г.\r\nВитамины: витамин В1 (тиамин) – 0,6 мг., витамин В2 (рибофлавин) – 0,1 мг.,\r\nвитамин В3 или витамин РР (ниацин) – 12,1 мг., витамин В4 (холин) – 52,5 мг.,\r\nвитамин В5 (пантотеновая кислота) – 1,8 мг., витамин В6 (пиридоксин) – 0,4 мг.,\r\nвитамин В9 (фолиевая кислота) – 250,0 мкг., витамин E (токоферол) – 8,3 мг.\r\nЭнергетическая ценность 100 г ядер арахиса – около 567,0 кКал / 2.372 кДж.\r\n', 0, NULL),
(349, 'Арахис неочищенный, жареный, Россия', 11, 16, 150.00, 1, 3, 'Арахис неочищенный, жареный, Россия', 'Плантации арахиса представляют из себя ряды невысокой ветвящейся травы (до 50 см), похожей на фасоль или горох. Вначале на его тонких стебельках распускаются желто-оранжевые цветки, а после опыления начинается увядание и одновременно формирование завязи. Цветки постепенно опускаются вниз, зарываются в землю на глубину 5-15 см, и уже там, в темноте, происходит формирование плода – боба, в котором находятся до 5 семян, по своей структуре и химическому составу схожих с орехами.', 0, NULL),
(350, 'Арахис очищенный сырой 28/32, 34/38', 11, 16, 118.00, 1, 3, 'Арахис очищенный сырой 28/32, 34/38, Китай, Аргентина, Индия, Бразилия ', 'Благодаря своей невысокой цене и отличным вкусовым качествам арахис является одним из наиболее популярных и часто используемых орехов. Основная область применения – кондитерская и пищевая промышленность.\r\nПрекрасно проявляет себя в любой разновидности теста, поэтому большой популярностью пользуется выпечка с арахисом – печенье, кексы, пряники, вафли, рулеты, пироги и др. Отлично сочетается с молочными продуктами, морепродуктами и мясом.\r\nЭнергетическая ценность 100 г арахиса – около 567,0 кКал / 2.372 кДж.', 0, NULL),
(351, 'Арахис очищенный сырой 38/42, Аргентина', 11, 16, 118.00, 1, 3, 'Арахис очищенный сырой 38/42, Аргентина', 'Идеальный ингредиент при изготовлении кондитерских изделий (наполнители, начинки и кремы для конфет, шоколада и шоколадных батончиков, тортов), продуктов быстрого приготовления (сухие завтраки, снеки, мюсли, кранчи, хлопья, подушечки), мороженого, энергетических батончиков и биологически активных добавок (например, добавляется в гематоген).', 0, NULL),
(352, 'Арахис очищенный сырой 40/50, Аргентина', 11, 16, 110.00, 1, 3, 'Арахис очищенный сырой 40/50, Аргентина', 'Прекрасно проявляет себя в любой разновидности теста, поэтому большой популярностью пользуется выпечка с арахисом – печенье, кексы, пряники, вафли, рулеты, пироги и др. Отлично сочетается с молочными продуктами, морепродуктами и мясом.\r\nЭнергетическая ценность 100 г арахиса – около 567,0 кКал / 2.372 кДж.', 0, NULL),
(353, 'Арахис очищенный сырой 50/60, Аргентина', 11, 16, 113.00, 1, 3, 'Арахис очищенный сырой 50/60, Аргентина', 'Благодаря своей невысокой цене и отличным вкусовым качествам арахис является одним из наиболее популярных и часто используемых орехов. Основная область применения – кондитерская и пищевая промышленность.\r\nВ натуральном виде, обжаренный, дробленый, резаный на кусочки, перетертый, в сахаре и различных сиропах, соках и глазурях, с солью и специями – область использования арахиса практически безгранична.', 0, NULL),
(354, 'Арахис очищенный сырой половинки, Аргентина', 11, 16, 118.00, 1, 3, 'Арахис очищенный сырой половинки, Аргентина', 'Идеальный ингредиент при изготовлении кондитерских изделий (наполнители, начинки и кремы для конфет, шоколада и шоколадных батончиков, тортов), продуктов быстрого приготовления (сухие завтраки, снеки, мюсли, кранчи, хлопья, подушечки), мороженого, энергетических батончиков и биологически активных добавок (например, добавляется в гематоген).', 0, NULL),
(355, 'Арахис очищенный, Узбекистан', 11, 16, 150.00, 1, 3, 'Арахис очищенный, Узбекистан', 'Отличительная особенность узбекского арахиса в том, что он слаще своих собратьев из других стран. После недолгого замачивания (на 2-3 часа) он становится еще более сладким и сочным. Тогда же можно легко снять кожицу. А если позволить арахису немного прорасти, оставив его в воде часов на пять, он будет как нельзя лучше подготовлен для приготовления конфет, паст и многих других блюд.', 0, NULL),
(356, 'Бразильский орех, Перу', 11, 16, 770.00, 1, 3, 'Бразильский орех, Перу', 'Бразильские орехи полюбились потребителям практически сразу, как только появились на нашем рынке. В косметологии очень ценится масло бразильского ореха, но основное его предназначение – употребление в пищу. Благодаря своей питательности и калорийности бразильские орехи широко используются как снеки, как в сыром, так и поджаренном и даже соленом виде. Их добавляют в выпечку, сладости, шоколад и мороженое, а также в салаты, закуски, супы, вторые блюда и соусы.\r\nБразильские орехи также ценятся за то, что при соблюдении правил хранения (в сухом прохладном месте, в закрытом виде) они даже без скорлупы не теряют своих свойств до двух лет.\r\nВ 100 г бразильских орехов содержится: вода – 4,3 г., белки – 13,6 г., жиры – 66,8 г.,\r\nуглеводы – 3,6 г., пищевые волокна (клетчатка) – 8,1 г.\r\nВитамины: витамин В1 (тиамин) – 1,0 мг., витамин В2 (рибофлавин) – 0,03 мг.,\r\nвитамин В3 или витамин РР (ниацин) – 0,2 мг., витамин В4 (холин) – 28,8 мг.,\r\nвитамин В5 (пантотеновая кислота) – 0,2 мг., витамин В6 (пиридоксин) – 0,1 мг.,\r\nвитамин В9 (фолиевая кислота) – 40,0 мкг., витамин С (аскорбиновая кислота) – 0,7 мг., витамин Е (токоферол) – 7,6 мг., витамин Н (биотин) – 6,5 мкг.\r\nЭнергетическая ценность 100 г бразильских орехов – около 660,0 кКал / 2.761 кДж.', 0, NULL),
(357, 'Кедровый орех очищенный, Россия', 11, 16, 2090.00, 1, 3, 'Кедровый орех очищенный, Россия', 'В медицине кедровые орехи и кедровое масло используются при лечении целого ряда заболеваний, а в кулинарии – во множестве различных блюд и кондитерских изделий (печенье, кексы и др.), как в сыром, так и жареном виде.\r\nПри нагревании кедровые орехи выпускают масло, и в таком виде они являются прекрасными ингредиентами для мясных, рыбных, овощных блюд и салатов, соусов (в частности, известный итальянский соус «песто»). Кулинары утверждают, что в некоторых случая кедровые орехи по своей ценности и питательности могут заменить мясо.\r\nВ 100 г кедровых орехов содержится: вода – 6,4 г.,  белки – 24,0 г., жиры – 50,7 г.,\r\nуглеводы – 7,3 г., пищевые волокна (клетчатка) – 7,2 г.\r\nВитамины: витамин А (бета-каротин) – 18,0 мкг., витамин В1 (тиамин) – 0,8 мг.,\r\nвитамин В2 (рибофлавин) – 0,2 мг., витамин В3 или витамин РР (ниацин) – 3,6 мг.,\r\nвитамин В5 (пантотеновая кислота) – 0,02 мг., витамин В6 (пиридоксин) – 0,11 мг., \r\nвитамин В9 (фолиевая кислота) – 57,0 мкг., витамин С (аскорбиновая кислота) – 1,9 мг., витамин Е (токоферол) – 13,7 мг., витамин H (биотин) – 10,0 мкг.\r\nЭнергетическая ценность 100 г кедровых орехов – около 575,0 кКал / 2.406 кДж.', 0, NULL),
(358, 'Кешью жареный, Вьетнам', 11, 16, 850.00, 1, 3, 'Кешью жареный, Вьетнам', 'Вкус орехов кешью сладковатый и очень приятный. Они очень распространены в азиатской и индийской кухне. Кешью улучшают вкус любого блюда – супов, соусов, риса, приправ, салатов, выпечки. Их добавляют в мясные и овощные блюда и, конечно же, разнообразные десерты.\r\nЭти орехи часто употребляют самостоятельно или в составе закуски к пиву, вину, коктейлям. Их обжаривают с солью, медом, карамелизуют в сахаре. Сладкие орешки добавляют к фруктовым салатам, в десерты, посыпают мороженое.\r\nВ 100 г кешью содержится: вода – 5,3 г., белки – 18,5 г., жиры – 48,5 г., углеводы – 22,5 г., пищевые волокна (клетчатка) – 2,0 г.\r\nВитамины: витамин В1 (тиамин) – 0,2 мг., витамин В2 (рибофлавин) – 0,2 мг., \r\nвитамин В3 или витамин РР (ниацин) – 4,7 мг., витамин В5 (пантотеновая кислота) – 1,2 мг., витамин В6 (пиридоксин) – 0,3 мг., витамин В9 (фолиевая кислота) – 69,7 мкг., витамин Е (токоферол) – 7,5 мг., витамин К (филлохинон) – 34,9 мкг., \r\nвитамин H (биотин) – 13,1 мкг.\r\nЭнергетическая ценность 100 г кешью – около 600,0 кКал / 2.510 кДж.', 0, NULL),
(359, 'Кешью сушеный, Вьетнам', 11, 16, 640.00, 1, 3, 'Кешью сушеный DW360, Вьетнам', 'Эти орехи часто употребляют самостоятельно или в составе закуски к пиву, вину, коктейлям. Их обжаривают с солью, медом, карамелизуют в сахаре. Сладкие орешки добавляют к фруктовым салатам, в десерты, посыпают мороженое.', 0, NULL),
(361, 'Кешью сушеный W320', 11, 16, 758.00, 1, 3, 'Кешью сушеный W320, Вьетнам', 'Орехи кешью являются плодами дерева анакардиум, вырастающего высотой до 10 м. Точнее плодом дерева является сочный оранжевый кисло-сладкий фрукт с очень коротким сроком хранения (не более суток-двух), из которого и вырастает изогнутый отросток в тонкой оболочке коричнево-красного или розового цвета – орех кешью. Скорлупу с орехов снимают вручную, ядра подсушивают и затем тщательно удаляют оболочку.', 0, NULL),
(362, 'Кешью сушеный W450, Вьетнам', 11, 16, 700.00, 1, 3, 'Кешью сушеный W450 Вьетнам', 'Родиной орехов кешью является Бразилия, где до сих пор встречаются дикорастущие заросли деревьев. Культивируют кешью и других в теплолюбивых странах – Индии, Вьетнаме, Бразилии, Иране, Азербайджане, странах Африки.', 0, NULL),
(363, 'Кешью сушеный WS, Вьетнам', 11, 16, 670.00, 1, 3, 'Кешью сушеный WS, Вьетнам', 'Вкус орехов кешью сладковатый и очень приятный. Они очень распространены в азиатской и индийской кухне. Кешью улучшают вкус любого блюда – супов, соусов, риса, приправ, салатов, выпечки. Их добавляют в мясные и овощные блюда и, конечно же, разнообразные десерты.', 0, NULL),
(364, 'Миндаль в сахаре, Россия', 11, 16, 450.00, 1, 3, 'Миндаль в сахаре, Россия', 'Миндаль в сахаре – очень калорийный и сытный продукт. Зачастую используется для небольших перекусов, а также как начинка и украшение для выпечки и кондитерских изделий. Прекрасное дополнение к горячему чаю и кофе.\r\nВ 100 г миндаля содержится:\r\nвода – 4,0 г\r\nбелки – 18,6 г\r\nжиры – 57,7 г\r\nуглеводы – 13,0 г\r\nпищевые волокна (клетчатка) – 7,0 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 20,0 мкг\r\nвитамин В1 (тиамин) – 0,3 мг\r\nвитамин В2 (рибофлавин) – 0,7 мг\r\nвитамин В3 или витамин РР (ниацин) – 4,0 мг\r\nвитамин В5 (пантотеновая кислота) – 0,4 мг\r\nвитамин В6 (пиридоксин) – 0,3 мг\r\nвитамин В9 (фолиевая кислота) – 40,0 мкг\r\nвитамин C (аскорбиновая кислота) – 1,5 мг\r\nвитамин E (токоферол) – 30,8 мг\r\nЭнергетическая ценность 100 г миндаля – около 645,0 кКал / 2.699 кДж.', 0, NULL),
(365, 'Миндаль жареный, Россия', 11, 16, 850.00, 1, 3, 'Миндаль жареный, Россия', 'По мнению большинства потребителей, идеальной базой для добавления миндаля является шоколад высших сортов. Кроме того, все больший рост показывает производство миндального молока и миндального масла, которые тоже могут использоваться при создании кондитерских изделий. Они отлично подходят для изготовления продуктов, не содержащих молочных компонентов и глютена, что давно стало одним из трендов здорового питания.', 0, NULL),
(366, 'Миндаль жареный соленый в скорлупе', 11, 16, 750.00, 1, 3, 'Миндаль жареный соленый в скорлупе, Россия', 'Жареный соленый миндаль в скорлупе – очень калорийный и сытный продукт. Зачастую он используется для небольших перекусов, за что ценится производителями снековой продукции. Также это прекрасное дополнение к слабоалкогольным и алкогольным напиткам.\r\nВ 100 г жареного соленого миндаля содержится:\r\nвода – 2,5 г\r\nбелки – 21,1 г\r\nжиры – 52,1 г\r\nуглеводы – 10,3 г\r\nпищевые волокна (клетчатка) – 10,9 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 1,0 мкг\r\nвитамин В1 (тиамин) – 0,1 мг\r\nвитамин В2 (рибофлавин) – 1,0 мг\r\nвитамин В3 или витамин РР (ниацин) – 3,6 мг\r\nвитамин В4 (холин) – 52,1 мг\r\nвитамин В5 (пантотеновая кислота) – 0,3 мг\r\nвитамин В6 (пиридоксин) – 0,1 мг\r\nвитамин В9 (фолиевая кислота) – 53,0 мкг\r\nвитамин E (токоферол) – 23,8 мг\r\nЭнергетическая ценность 100 г жареного соленого миндаля – около 595,0 кКал / 2.489 кДж.', 0, NULL),
(367, 'Миндаль Калифорния 20/22, 22/24, 23/25', 11, 16, 685.00, 1, 3, 'Миндаль Калифорния 20/22, 22/24, 23/25, США', 'Миндаль употребляется в сыром или обжаренном виде, целый, дробленый, резаный лепестками для приготовления и украшения печенья, тортов, пирожных и сладких блюд. Молотый миндаль используется при изготовлении паст и наполнителей, эссенций для ароматизации пирожных и соусов, в качестве посыпки. Из миндальной муки также готовят известные пирожные макаронс. Еще одно известное во всем мире лакомство на основе тертого миндаля – марципановая масса.', 0, NULL),
(368, 'Миндаль Калифорния 27/30, США', 11, 16, 675.00, 1, 3, 'Миндаль Калифорния 27/30, США', 'Миндаль – самая распространенная орехоплодная культура в Калифорнии. Местные фермеры обеспечивают до 100% потребность в миндале в США и 80% – в мире. Насчитывается около 25 разновидностей данного растения с разными сроками созревания и качественными характеристиками.', 0, NULL),
(369, 'Миндаль Калифорния 30/32, 32/34, 34/36', 11, 16, 675.00, 1, 3, 'Миндаль Калифорния 30/32, 32/34, 34/36, США', 'По мнению большинства потребителей, идеальной базой для добавления миндаля является шоколад высших сортов. Кроме того, все больший рост показывает производство миндального молока и миндального масла, которые тоже могут использоваться при создании кондитерских изделий. Они отлично подходят для изготовления продуктов, не содержащих молочных компонентов и глютена, что давно стало одним из трендов здорового питания.', 0, NULL),
(370, 'Миндаль орехи Кармель (Carmel)', 11, 16, 750.00, 1, 3, 'Миндаль Кармель орех (Carmel), Чили', 'Миндаль группы Кармель созревает в сентябре. Орехи данного сорта средних размеров, узкой формы со слегка морщинистой поверхностью. Оболочка мягкая, кожица достаточно легко отделяется, поэтому миндаль Кармель хорошо подходит для бланширования и идеален для жарки.\r\nВ 100 г миндаля содержится:\r\nвода – 4,0 г\r\nбелки – 18,6 г\r\nжиры – 57,7 г\r\nуглеводы – 13,0 г\r\nпищевые волокна (клетчатка) – 7,0 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 20,0 мкг\r\nвитамин В1 (тиамин) – 0,3 мг\r\nвитамин В2 (рибофлавин) – 0,7 мг\r\nвитамин В3 или витамин РР (ниацин) – 4,0 мг\r\nвитамин В5 (пантотеновая кислота) – 0,4 мг\r\nвитамин В6 (пиридоксин) – 0,3 мг\r\nвитамин В9 (фолиевая кислота) – 40,0 мкг\r\nвитамин C (аскорбиновая кислота) – 1,5 мг\r\nвитамин E (токоферол) – 30,8 мг\r\nЭнергетическая ценность 100 г миндаля – около 645,0 кКал / 2.699 кДж.', 1, 'FTzO85xUqyJsdWLSoXtU.jpg'),
(371, 'Миндаль Кармель (Carmel) 25/27, 27/30', 11, 16, 675.00, 1, 3, 'Миндаль Кармель (Carmel) 25/27, 27/30 ,США', 'Миндаль группы Кармель созревает в сентябре. Орехи данного сорта средних размеров, узкой формы со слегка морщинистой поверхностью. Оболочка мягкая, кожица достаточно легко отделяется, поэтому миндаль Кармель хорошо подходит для бланширования и идеален для жарки.\r\nПо мнению большинства потребителей, идеальной базой для добавления миндаля является шоколад высших сортов. Кроме того, все больший рост показывает производство миндального молока и миндального масла, которые тоже могут использоваться при создании кондитерских изделий. Они отлично подходят для изготовления продуктов, не содержащих молочных компонентов и глютена, что давно стало одним из трендов здорового питания.', 0, NULL),
(372, 'Миндаль Нонпарель (Nonpareil, N/P) 22/24, 23/25', 11, 16, 715.00, 1, 3, 'Миндаль Нонпарель (Nonpareil, N/P) 22/24, 23/25, США', 'Разновидность миндаля раннего периода созревания (конец августа).\r\nМиндаль группы Нонпарель (N/P) отличают крупный размер, довольно мягкая скорлупа, удлиненная форма, гладкая поверхность и светлая кожица самого ядра. Благодаря этим свойствам миндаль Нонпарель меньше повреждается во время очистки от скорлупы, поэтому пользуется большой популярностью у представителей пищевой промышленности (производство снеков, кондитерских изделий и др.), где решающее значение имеет внешний вид ядер.\r\nЗа счет гладкой кожицы миндаль Нонпарель идеально подходит для бланширования, а удлиненная форма делает его удобным при производстве миндальных лепестков.', 0, NULL),
(373, 'Миндаль Нонпарель (Nonpareil, N/P) 25/27, 27/30', 11, 16, 705.00, 1, 3, 'Миндаль Нонпарель (Nonpareil, N/P) 25/27, 27/30, США', 'Разновидность миндаля раннего периода созревания (конец августа).\r\nМиндаль группы Нонпарель (N/P) отличают крупный размер, довольно мягкая скорлупа, удлиненная форма, гладкая поверхность и светлая кожица самого ядра. Благодаря этим свойствам миндаль Нонпарель меньше повреждается во время очистки от скорлупы, поэтому пользуется большой популярностью у представителей пищевой промышленности (производство снеков, кондитерских изделий и др.), где решающее значение имеет внешний вид ядер.', 0, NULL),
(374, 'Миндаль Нонпарель (Nonpareil, N/P) 30/32', 11, 16, 695.00, 1, 3, 'Миндаль Нонпарель (Nonpareil, N/P) 30/32, США', 'По мнению большинства потребителей, идеальной базой для добавления миндаля является шоколад высших сортов. Кроме того, все больший рост показывает производство миндального молока и миндального масла, которые тоже могут использоваться при создании кондитерских изделий. Они отлично подходят для изготовления продуктов, не содержащих молочных компонентов и глютена, что давно стало одним из трендов здорового питания.', 0, NULL),
(375, 'Фисташки жареные соленые (ж/с) механика, Иран', 11, 16, 770.00, 1, 3, 'Фисташки жареные соленые (ж/с) механика, Иран', 'Фисташковые деревья растут в некоторых странах Азии и на северо-западе Африки. Внешний вид фисташек известен всем – это твердая светло-серая скорлупа в форме коробочки, внутри которой содержится ядро, покрытое оболочкой зеленого цвета.\r\nПри созревании не все фисташковые коробочки раскрываются одновременно, и перед употреблением их подвергают механической обработке, в процессе которой они дораскрываются, поэтому их называют фисташками механического раскрытия. По своим вкусовым качествам механическая фисташка практически ничем не уступает фисташке натурального открытия, просто ядро такой фисташки немного мельче.', 0, NULL),
(376, 'Фисташки жареные соленые (ж/с) натуральные, Иран', 11, 16, 800.00, 1, 3, 'Фисташки жареные соленые (ж/с) натуральные, Иран', 'В пищевой индустрии фисташки используются в сыром и обжаренном виде, растертые и дробленые. Соленые фисташки – одни из самых популярных орехов для употребления в самостоятельном виде и в качестве закуски к различным напиткам.\r\nВ 100 г фисташек содержится:\r\nвода – 7,0 г\r\nбелки – 20,6 г\r\nжиры – 44,4 г\r\nуглеводы – 17,7 г\r\nпищевые волокна (клетчатка) – 10,3 г\r\nВитамины:\r\nвитамин А (бета-каротин) – 0,3 мг\r\nвитамин В1 (тиамин) – 0,9 мг\r\nвитамин В2 (рибофлавин) – 0,2 мг\r\nвитамин В3 или витамин РР (ниацин) – 1,3 мг\r\nвитамин В5 (пантотеновая кислота) – 0,5 мг\r\nвитамин В6 (пиридоксин) – 1,7 мг\r\nвитамин В9 (фолиевая кислота) – 51,0 мкг \r\nвитамин С (аскорбиновая кислота) – 5,0 мг\r\nвитамин Е (токоферол) – 22,6 мг\r\nЭнергетическая ценность 100 г фисташек – около 557,0 кКал / 2.330 кДж.', 0, NULL),
(377, 'Фисташки жареные соленые натуральные, Иран', 11, 16, 900.00, 1, 3, 'Фисташки жареные соленые натуральные, Иран', 'В пищевой индустрии фисташки используются в сыром и обжаренном виде, растертые и дробленые. Соленые фисташки – одни из самых популярных орехов для употребления в самостоятельном виде и в качестве закуски к различным напиткам.', 1, 'QIkCjtUhgoShZ0fZJLAj.jpg'),
(378, 'Орехи фисташки ядро, Иран', 11, 16, 1750.00, 1, 3, 'Орехи фисташки ядро, Иран', 'В пищевой индустрии фисташки используются в сыром и обжаренном виде, растертые и дробленые. Благодаря своему приятному сладковатому вкусу они часто применяются для добавки в мучные и кондитерские изделия, шоколад и конфеты, а красивый зеленоватый цвет способствует использованию фисташек в качестве украшения для кондитерских изделий, посыпки для тортов и пирожных.\r\nКонечно, не стоит забывать и о том, что фисташки – одни из самых популярных орехов для употребления в самостоятельном виде и в качестве закуски к различным напиткам.', 1, 'OWK_h3C7MPtAnXgSV5n8.jpg'),
(379, 'Орех макадамия, Австралия', 11, 16, 850.00, 1, 3, 'Орех макадамия, Австралия', 'Макадамия на данный момент является самым дорогим орехом в мире. Многие источники утверждают, что ее выращивают меньше, чем производят черной икры. Плодоносить дерево макадамии начинает на 7-м, а иногда и 10-м году жизни, но жить может до 100 лет. Орехи имеют практически идеальную шарообразную форму и в диаметре обычно бывают 1,5-2 см. Ядро ореха находится под очень жесткой и плохо отделяемой скорлупой. На фоне того, что их еще и очень непросто собирать, вполне объяснима столь высокая стоимость макадамии.\r\nВ медицине и косметологии очень распространено масло макадамии. В кулинарии не меньшее применение, чем масло, находит и само ядро. По вкусу оно немного напоминает лесной орех, но гораздо питательнее его.\r\nСпособов употребления макадамии очень много. Помимо самостоятельного использования, с орехами готовят изысканные десерты. Например, ядра обжаривают и покрывают шоколадом или карамелью. Маленькие или дробленые орехи добавляют в салаты или блюда с морепродуктами. Макадамия прекрасно сочетается с винами (например, с хересом), а также кофе и чаем.\r\nВ 100 г орехов макадамии содержится:\r\nвода – 1,4 г\r\nбелки – 7,9 г\r\nжиры – 75,8 г\r\nуглеводы – 4,6 г\r\nпищевые волокна (клетчатка) – 8,6 г\r\nВитамины:\r\nвитамин В1 (тиамин) – 1,2 мг\r\nвитамин В2 (рибофлавин) – 0,2 мг\r\nвитамин В3 или витамин РР (ниацин) – 2,5 мг\r\nвитамин В5 (пантотеновая кислота) – 0,8 мг\r\nвитамин В6 (пиридоксин) – 0,3 мг\r\nвитамин В9 (фолиевая кислота) – 11,0 мкг \r\nвитамин С (аскорбиновая кислота) – 1,2 мг\r\nвитамин Е (токоферол) – 0,5 мг\r\nЭнергетическая ценность 100 г макадамии – около 718,0 кКал / 3.004 кДж.', 0, NULL),
(380, 'Фундук неочищенный, Россия', 11, 16, 290.00, 1, 3, 'Фундук неочищенный, Россия', 'Фундук (другие названия – лесной орех, лещина) – один из самых любимых и часто используемых орехов. В кулинарии фундук используется в сыром и обработанном виде: в салатах, соусах, мясных, овощных и рыбных блюдах, но чаще всего в сладких блюдах, кондитерских изделиях и выпечке, тортах и пирожных. Также фундук добавляют в некоторые виды хлеба.\r\nВ пищевой промышленности фундук используется в производстве шоколада, крема, ореховой пасты, конфет, ликеров.\r\nРегионы происхождения: Турция, Азербайджан', 0, NULL),
(383, 'Фундук очищенный, Россия', 11, 16, 740.00, 1, 3, 'Фундук очищенный, Россия', 'Фундук (другие названия – лесной орех, лещина) – один из самых любимых и часто используемых орехов. Ядра покрыты тонкой коричневой оболочкой, которая легко снимается: следует всего лишь положить их на несколько минут в духовку, а затем потереть между ладонями – и оболочка полностью отделится.\r\nВ кулинарии фундук используется в сыром и обработанном виде, широко применяется в целом, дробленом или растертом виде при изготовлении мучных и кондитерских изделий, грильяжа и восточных сладостей, конфетных масс, шоколада с орехами, карамельных и вафельных начинок, некоторых видов хлеба и ликеров.', 0, NULL),
(384, 'Фундук орех очищенный не жареный, Россия', 11, 16, 740.00, 1, 3, 'Фундук очищенный не жареный, Россия', 'В 100 г фундука содержится:\r\nвода – 5,3 г\r\nбелки – 15,0 г\r\nжиры – 60,8 г\r\nуглеводы – 7,0 г\r\nпищевые волокна (клетчатка) – 9,7 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 11,0 мкг\r\nвитамин В1 (тиамин) – 0,6 мг\r\nвитамин В2 (рибофлавин) – 0,1 мг\r\nвитамин В3 или витамин РР (ниацин) – 1,8 мг\r\nвитамин В5 (пантотеновая кислота) – 0,9 мг\r\nвитамин В6 (пиридоксин) – 0,6 мг\r\nвитамин В9 (фолиевая кислота) – 113,0 мкг\r\nвитамин C (аскорбиновая кислота) – 6,3 мг\r\nвитамин E (токоферол) – 15,0 мг\r\nвитамин K (менахинон) – 14,2 мкг\r\nЭнергетическая ценность 100 г фундука – около 707,0 кКал / 2.958 кДж.', 1, 'zOzXY1OUOgG782L42BuN.jpg'),
(385, 'Фундук очищенный 15+, 15/17, 17+, Россия', 11, 16, 560.00, 1, 3, 'Фундук очищенный 15+, 15/17, 17+, Россия', 'Макроэлементы:\r\nкалий – 680 мг\r\nкальций – 114 мг\r\nмагний – 13 мг\r\nфосфор – 290 мг\r\nМикроэлементы:\r\nжелезо – 4,7 мг\r\nмарганец – 6,2 мг\r\nмедь – 1,7 мг\r\nселен – 2,4 мкг\r\nцинк – 2,5 мг\r\nЭнергетическая ценность 100 г фундука – около 707,0 кКал / 2.958 кДж.', 0, NULL),
(386, 'Фундук очищенный 9/11, Россия', 11, 16, 560.00, 1, 3, 'Фундук очищенный 9/11, Россия', 'Фундук (другие названия – лесной орех, лещина) – один из самых любимых и часто используемых орехов. Ядра покрыты тонкой коричневой оболочкой, которая легко снимается: следует всего лишь положить их на несколько минут в духовку, а затем потереть между ладонями – и оболочка полностью отделится.\r\nВ кулинарии фундук используется в сыром и обработанном виде, широко применяется в целом, дробленом или растертом виде при изготовлении мучных и кондитерских изделий, грильяжа и восточных сладостей, конфетных масс, шоколада с орехами, карамельных и вафельных начинок, некоторых видов хлеба и ликеров.', 0, NULL),
(387, 'Фундук орех очищенный жареный, Россия', 11, 16, 800.00, 1, 3, 'Фундук орех очищенный жареный, Россия', 'Фундук (другие названия – лесной орех, лещина) – один из самых любимых и часто используемых орехов. Ядра покрыты тонкой коричневой оболочкой, которая легко снимается: следует всего лишь положить их на несколько минут в духовку, а затем потереть между ладонями – и оболочка полностью отделится.\r\nВ кулинарии фундук используется в сыром и обработанном виде, причем в поджаренном виде фундук вкуснее, чем в сыром. Фундук широко применяется в целом, дробленом или растертом виде при изготовлении мучных и кондитерских изделий, грильяжа и восточных сладостей, конфетных масс, шоколада с орехами, карамельных и вафельных начинок, некоторых видов хлеба и ликеров.', 1, 'zOzXY1OUOgG782L42BuN.jpg'),
(388, 'Фундук очищенный бланшированный нежареный, Россия', 11, 16, 120.00, 1, 3, 'Фундук очищенный бланшированный нежареный, Россия', 'Ядра орехов фундука без семенной оболочки, бланшированные, целые, нормально развитые, круглые или слегка вытянутые, однородные, имеющие характерные внешний вид и размеры для данной разновидности.\r\n\r\nЭнергетическая ценность 100 г фундука – около 707,0 кКал / 2.958 кДж.', 0, NULL),
(389, 'Фундук очищенный, половинки, Россия', 11, 16, 480.00, 1, 3, 'Фундук очищенный, половинки, Россия', 'В кулинарии фундук используется в сыром и обработанном виде, широко применяется в целом, дробленом или растертом виде при изготовлении мучных и кондитерских изделий, грильяжа и восточных сладостей, конфетных масс, шоколада с орехами, карамельных и вафельных начинок, некоторых видов хлеба и ликеров.', 0, NULL),
(390, 'Пекан сушеный, Мексика', 11, 16, 1450.00, 1, 3, 'Пекан сушеный, Мексика', 'Орехи пекан произрастают в Северной Америке. Из-за своей гладкой скорлупы они напоминают либо лесной орех, либо плод оливки, поэтому их часто называют «оливковыми орехами». Пекан также считается родственником грецкого ореха: их ядра очень по форме, только более вытянуты. По вкусу пекан тоже похож на грецкий орех, только мягче и нежнее. Отличаются эти орехи тем, что внутри скорлупы почти нет перегородок, поэтому их очень легко извлекать и они не горчат.\r\nОрехи пекан добавляют в тесто, начинки, используют для украшения мучных и кондитерских изделий.\r\nВ 100 г ореха пекан содержится:\r\nвода – 3,5 г\r\nбелки – 9,2 г\r\nжиры – 72,0 г\r\nуглеводы – 4,3 г\r\nпищевые волокна (клетчатка) – 9,6 г\r\nВитамины:\r\nвитамин А (бета-каротин) – 29,0 мкг\r\nвитамин В1 (тиамин) – 0,7 мг\r\nвитамин В2 (рибофлавин) – 0,1 мг\r\nвитамин В3 или витамин РР (ниацин) – 1,2 мг\r\nвитамин В4 (холин) – 40,5 мг\r\nвитамин В5 (пантотеновая кислота) – 0,9 мг\r\nвитамин В6 (пиридоксин) – 0,2 мг\r\nвитамин В9 (фолиевая кислота) – 22,0 мкг \r\nвитамин С (аскорбиновая кислота) – 1,1 мг\r\nвитамин Е (токоферол) – 1,4 мг\r\nвитамин K (филлохинон) – 3,5 мкг\r\nЭнергетическая ценность 100 г пекана – около 691,0 кКал / 2.891 кДж.', 1, 'mJP_fKe_w4CZf7sKyPTJ.jpg'),
(391, 'Абрикос сушеный резаный, Россия', 11, 16, 235.00, 1, 3, 'Абрикос сушеный резаный, Россия', 'Область применения сушеных абрикосов чрезвычайно широка. Они используются для приготовления десертов, пирожных, печенья, пирогов; добавляются в мюсли, кранчи, энергетические батончики, салаты, соусы, к мясу и рыбе для придания блюдам кисло-сладкого вкуса и нежного аромата. Для этого сушеные абрикосы необходимо тщательно измельчить.\r\nПредприятия пищевой промышленности зачастую покупают уже резаные абрикосы с кусочками необходимого им размера, что избавляет их от необходимости покупки дополнительного оборудования и значительно ускоряет технологический процесс\r\nВ 100 г сушеных абрикосов содержится:\r\nвода – 30,1 г\r\nбелки – 3,4 г\r\nжиры – 0,5 г\r\nуглеводы – 62,6 г\r\nпищевые волокна (клетчатка) – 7,3 г\r\nорганические кислоты – 1,5 г\r\nВитамины:\r\nвитамин А (бета-каротин) – 3,5 мг\r\nвитамин В1 (тиамин) – 0,02 мг\r\nвитамин В2 (рибофлавин) – 0,07 мг\r\nвитамин В3 или витамин РР (ниацин) – 2,6 мг\r\nвитамин В4 (холин) – 13,9 мг\r\nвитамин В5 (пантотеновая кислота) – 0,5 мг\r\nвитамин В6 (пиридоксин) – 0,1 мг\r\nвитамин В9 (фолиевая кислота) – 10,0 мкг\r\nвитамин C (аскорбиновая кислота) – 1,0 мг\r\nвитамин Е (токоферол) – 4,3 мг\r\nвитамин К (филлохинон) – 3,1 мкг\r\nЭнергетическая ценность 100 г сушеных абрикосов – около 241,0 кКал / 1.008 кДж.', 0, NULL),
(392, 'Арахис бланшированный, Россия', 11, 16, 200.00, 1, 3, 'Арахис бланшированный, Россия', 'В натуральном виде, обжаренный, дробленый, резаный на кусочки – область использования арахиса практически безгранична. Это идеальный ингредиент при изготовлении кондитерских изделий (наполнители, начинки и кремы для конфет, шоколада и шоколадных батончиков, тортов), продуктов быстрого приготовления (сухие завтраки, снеки, мюсли, кранчи, хлопья, подушечки), мороженого, энергетических батончиков и биологически активных добавок (например, добавляется в гематоген).\r\nПрекрасно проявляет себя в любой разновидности теста, поэтому большой популярностью пользуется выпечка с арахисом – печенье, кексы, пряники, вафли, рулеты, пироги и др.\r\nОтлично сочетается с молочными продуктами, морепродуктами и мясом.\r\nВ 100 г обжаренного арахиса содержится:\r\nвода – 1,6 г\r\nбелки – 23,7 г\r\nжиры – 49,7 г\r\nуглеводы – 13,5 г\r\nпищевые волокна (клетчатка) – 8,0 г\r\nВитамины:\r\nвитамин В1 (тиамин) – 0,4 мг\r\nвитамин В2 (рибофлавин) – 0,1 мг\r\nвитамин В3 или витамин РР (ниацин) – 13,5 мг\r\nвитамин В4 (холин) – 55,3 мг\r\nвитамин В5 (пантотеновая кислота) – 1,4 мг\r\nвитамин В6 (пиридоксин) – 0,3 мг\r\nвитамин В9 (фолиевая кислота) – 145,0 мкг\r\nвитамин E (токоферол) – 6,9 мг\r\nЭнергетическая ценность 100 г обжаренного арахиса – около 585,0 кКал / 2.448 кДж.', 0, NULL),
(393, 'Арахис жареный солёный, Россия', 11, 16, 200.00, 1, 3, 'Арахис жареный солёный, Россия', 'Благодаря своей невысокой цене и отличным вкусовым качествам арахис является одним из наиболее популярных и часто используемых орехов. Основная область применения – кондитерская и пищевая промышленность.\r\nЛюбой вид обработки арахиса не портит, а, наоборот, заметно усиливает его собственный вкус и аромат и существенно обогащает общий вкус готовых продуктов. Наряду с большим содержанием жира, это позволяет значительно уменьшить добавление синтетических жиров и ароматизаторов, не снижая энергетической ценности продукции и повышая ее качество.', 1, 'ohzH4wPTVKnd-47nvo3Y.jpg'),
(394, 'Арахис жареный, Россия', 11, 16, 200.00, 1, 3, 'Арахис жареный, Россия', 'В натуральном виде, обжаренный, дробленый, резаный на кусочки – область использования арахиса практически безгранична. Это идеальный ингредиент при изготовлении кондитерских изделий (наполнители, начинки и кремы для конфет, шоколада и шоколадных батончиков, тортов), продуктов быстрого приготовления (сухие завтраки, снеки, мюсли, кранчи, хлопья, подушечки), мороженого, энергетических батончиков и биологически активных добавок (например, добавляется в гематоген).\r\nПрекрасно проявляет себя в любой разновидности теста, поэтому большой популярностью пользуется выпечка с арахисом – печенье, кексы, пряники, вафли, рулеты, пироги и др.\r\nОтлично сочетается с молочными продуктами, морепродуктами и мясом.', 1, '6QHykqndNQ61pnULEYC3.jpg'),
(395, 'Арахис очищенный, лепесток, Россия', 11, 16, 199.00, 1, 3, 'Арахис очищенный, лепесток, Россия', 'Любой вид обработки арахиса не портит, а, наоборот, заметно усиливает его собственный вкус и аромат и существенно обогащает общий вкус готовых продуктов. Наряду с большим содержанием жира, это позволяет значительно уменьшить добавление синтетических жиров и ароматизаторов, не снижая энергетической ценности продукции и повышая ее качество.\r\nВ натуральном виде, обжаренный, дробленый, резаный – область использования арахиса практически безгранична. Это идеальный ингредиент при изготовлении кондитерских изделий (наполнители, начинки и кремы для конфет, шоколада и шоколадных батончиков, тортов), продуктов быстрого приготовления (сухие завтраки, снеки, мюсли, кранчи, хлопья, подушечки), мороженого, энергетических батончиков и биологически активных добавок (например, добавляется в гематоген).', 0, NULL),
(396, 'Бананы резаные, Россия', 11, 16, 280.00, 1, 3, 'Бананы резаные, Россия', 'Технология приготовления сушеных бананов очень проста. Зрелые бананы очищают от кожуры, нарезают, раскладывают на противнях и вялят при небольшой температуре в печах на древесных углях. В процессе сушки влажность бананов существенно снижается и они заметно уменьшаются в размерах, правда, своих полезных свойств не теряют.\r\nОбласть использования резаных сушеных бананов достаточно широка. Их добавляют в каши, мюсли, выпечку (кексы, пироги, печенье, торты, пирожные и др.), сладкие соусы, компоты, энергетические батончики, молочные продукты (йогурты, пудинги, мороженое, кисломолочные напитки). Кроме того, сушеные бананы сами по себе выступают как вкусный и полезный перекус, за что и ценятся производителями снековой продукции.\r\nВ 100 г сушеных бананов содержится:\r\nвода – 4,3 г\r\nбелки – 2,3 г\r\nжиры – 33,6 г\r\nуглеводы – 57,6 г\r\nпищевые волокна (клетчатка) – 7,6 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 0,03 мг\r\nвитамин В1 (тиамин) – 0,1 мг\r\nвитамин В2 (рибофлавин) – 0,02 мг\r\nвитамин В3 или витамин РР (ниацин) – 0,7 мг\r\nвитамин В4 (холин) – 21,2 мг\r\nвитамин В5 (пантотеновая кислота) – 0,6 мг\r\nвитамин В6 (пиридоксин) – 0,3 мг\r\nвитамин В9 (фолиевая кислота) – 14,0 мкг\r\nвитамин C (аскорбиновая кислота) – 6,3 мг\r\nвитамин E (токоферол) – 0,2 мг\r\nвитамин K (филлохинон) – 1,3 мкг\r\nЭнергетическая ценность 100 г сушеных бананов – около 519,0 кКал / 2.171 кДж.', 0, NULL),
(397, 'Вишня резаная, Россия', 11, 16, 428.00, 1, 3, 'Вишня резаная, Россия.', 'Вишня – удивительная ягода: будучи кислой в свежем виде, после сушки она, наоборот, раскрывает свою сладость и при этом, в отличие от многих других ягод, не становится приторной.\r\nБлагодаря кисло-сладкому вкусу сушеная вишня прекрасно дополняет любую выпечку (кексы, булочки, куличи, пирожные, торты, печенье). Богатое содержание витаминов и минералов способствует активному использованию сушеной вишни в продуктах, относящихся к здоровому питанию, – мюсли, кисломолочные напитки и десерты, йогурты, энергетические батончики.\r\nСушеная вишня вкусна в чае, компотах и других напитках, а также соусах к мясным, рыбным и овощным блюдам. Довольно часто ее используют как альтернативу изюму.\r\nВ 100 г сушеной вишни содержится:\r\nвода – 45,2 г\r\nбелки – 1,8 г\r\nжиры – 0,02 г\r\nуглеводы – 71,9 г\r\nпищевые волокна (клетчатка) – 1,6 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 0,03 мг\r\nвитамин В1 (тиамин) – 0,02 мг\r\nвитамин В2 (рибофлавин) – 0,02 мг\r\nвитамин В3 или витамин РР (ниацин) – 0,4 мг\r\nвитамин В4 (холин) – 4,7 мг\r\nвитамин В5 (пантотеновая кислота) – 0,1 мг\r\nвитамин В6 (пиридоксин) – 0,03 мг\r\nвитамин В9 (фолиевая кислота) – 4,0 мкг\r\nвитамин C (аскорбиновая кислота) – 2,5 мг\r\nвитамин E (токоферол) – 0,1 мг\r\nвитамин K (филлохинон) – 1,4 мкг\r\nЭнергетическая ценность 100 г сушеной вишни – около 276,0 кКал / 1.155 кДж.', 0, NULL),
(398, 'Груша резаная, Россия', 11, 16, 300.00, 1, 3, 'Груша резаная, Россия', 'Для сушки отбираются свежие качественные груши, которые очищают, нарезают кусочками и затем сушат в специальных печах при температуре 60-80 °C. Так они сохраняют все полезные свойства и питательные вещества свежих фруктов.\r\nОсновная область применения сушеных груш – компоты и узвары, но резаные груши также активно используются для приготовления десертов, пирожных, печенья, пирогов; добавляются в мюсли, кранчи, энергетические батончики, салаты, соусы, к мясу и рыбе для придания блюдам кисло-сладкого вкуса и нежного аромата. И, конечно же, сушеные резаные груши вкусны и полезны сами по себе, как быстрый перекус, поэтому их часто добавляют в смеси с другими сушеными фруктами.\r\nВ 100 г сушеной груши содержится:\r\nвода – 24,0 г\r\nбелки – 2,3 г\r\nжиры – 0,6 г\r\nуглеводы – 62,6 г\r\nЭнергетическая ценность 100 г сушеной груши – около 270,0 кКал / 1.130 кДж.\r\nпищевые волокна (клетчатка) – 6,0 г\r\nВитамины:\r\nвитамин В1 (тиамин) – 0,03 мг\r\nвитамин В2 (рибофлавин) – 0,1 мг\r\nвитамин В3 или витамин РР (ниацин) – 0,5 мг\r\nвитамин C (аскорбиновая кислота) – 8,0 мг\r\nвитамин E (токоферол) – 0,4 мг\r\n', 0, NULL),
(399, 'Изюм б/к "JUMBO FLAME", Чили', 11, 16, 330.00, 1, 3, 'Изюм б/к "JUMBO FLAME", Чили', 'Изюм – высушенные плоды винограда – самый распространенный вид сухофруктов в нашей стране. Это уникальный продукт, потому что он максимально сохраняет полезные свойства свежего винограда – 70-80% витаминов и 100% микроэлементов.\r\nИзюм – настоящая находка для пищевой индустрии. Он прекрасно дополняет и обогащает вкус любых кондитерских изделий (конфеты, батончики, шоколад), продуктов быстрого приготовления (снеки, мюсли, кранчи), напитков, молочных продуктов (йогурты, пудинги, мороженое, кисломолочные напитки), мясных блюд. Изюм делает насыщенным и сбалансированным вкус выпечки, в особенности кексов и куличей, а также других хлебобулочных изделий (в том числе хлеба из разных видов муки).\r\nВ 100 г изюма содержится:\r\nвода – 15,4 г\r\nбелки – 3,2 г\r\nжиры – 0,5 г\r\nуглеводы – 73,7 г\r\nпищевые волокна (клетчатка) – 5,3 г\r\nВитамины:\r\nвитамин А (бета-каротин) – 6,0 мкг\r\nвитамин В1 (тиамин) – 0,2 мг\r\nвитамин В2 (рибофлавин) – 0,1 мг\r\nвитамин В3 или витамин РР (ниацин) – 0,8 мг\r\nвитамин В5 (пантотеновая кислота) – 0,6 мг\r\nвитамин В6 (пиридоксин) – 0,2 мг\r\nвитамин В9 (фолиевая кислота) – 3,3 мкг\r\nвитамин С (аскорбиновая кислота) – 3,3 мг\r\nвитамин E (токоферол) – 0,7 мг\r\nвитамин K (филлохинон) – 3,5 мкг\r\nвитамин H (биотин) – 2,0 мкг\r\nЭнергетическая ценность 100 г изюма – около 300,0 кКал / 1.255 кДж.', 1, 'auIMJVYVWn4O7gupTkzN.jpg'),
(400, 'Киви резаный', 11, 16, 440.00, 1, 3, 'Киви резаный', 'Киви часто используется в качестве ингредиента соусов для мясных и рыбных блюд. Мясо баранины, говядины, свинины, домашней птицы он делает нежным и сочным. По этой причине его добавляют в маринады для шашлыка, правда, держать мясо в таком маринаде можно не дольше часа.\r\nКиви также добавляют каши, гарниры и салаты, он прекрасно сочетается с гречкой, геркулесом, пшеном, рисом, орехами, творогом, йогуртом и другими кисломолочными продуктами.\r\nСушеный киви – отличный вариант для легкого и полезного перекуса, поэтому его часто добавляют в витаминные смеси с другими сухофруктами, энергетические батончики, а также продукты быстрого приготовления (мюсли, кранчи, хлопья и др.).\r\nВ 100 г сушеного киви содержится:\r\nвода – 16,7 г\r\nбелки – 0,8 г\r\nжиры – 0,3 г\r\nуглеводы – 85,0 г\r\nпищевые волокна (клетчатка) – 5,2 г\r\nВитамины:\r\nвитамин A (бета-каротин) – 0,1 мг\r\nвитамин В1 (тиамин) – 0,03 мг\r\nвитамин В2 (рибофлавин) – 0,03 мг\r\nвитамин В3 или витамин РР (ниацин) – 0,4 мг\r\nвитамин В6 (пиридоксин) – 0,2 мг\r\nвитамин В9 (фолиевая кислота) – 18,5 мкг\r\nвитамин C (аскорбиновая кислота) – 92,7 мг\r\nвитамин E (токоферол) – 0,3 мг\r\nЭнергетическая ценность 100 г сушеного киви – около 322,0 кКал / 1.347 кДж.', 0, NULL),
(401, 'Клубника резаная, Россия', 11, 16, 570.00, 1, 3, 'Клубника резаная, Россия', 'Резаную клубнику применяют в выпечке и кондитерских изделиях (кексы, печенье, пироги, торты, пирожные и др.), добавляют в чай, варят компоты, кисели, морсы, в том числе в сочетании с другими сушеными ягодами и фруктами. Резаную сушеную клубнику используют в начинках для блинов, как ингредиент муссах, коктейлях, мороженом и молочных коктейлях, добавляют в желе и муссы, а также используют для декора выпечки, сладостей и десертов.\r\nРезаную сушеную клубнику также применяют в кашах, гарнирах, овощных и фруктовых салатах, она сочетается с гречкой, овсянкой, пшеном, рисом, творогом, йогуртом и другими кисломолочными продуктами, орехами, сухофруктами и пряностями. Сушеная клубника идеально сочетается со сливками и сметаной.\r\nЭто отличный вариант для легкого и полезного перекуса, поэтому клубнику часто добавляют в витаминные смеси с другими сухофруктами, энергетические батончики, а также продукты быстрого приготовления (мюсли, кранчи, хлопья и др.).', 0, NULL),
(402, 'Клюква резаная, Россия', 11, 16, 490.00, 1, 3, 'Клюква резаная, Россия', 'Резаную клюкву применяют в выпечке и кондитерских изделиях (кексы, печенье, пироги, торты, пирожные и др.), добавляют в чай, варят компоты, кисели, морсы, в том числе в сочетании с другими сушеными ягодами и фруктами. Резаную сушеную клюкву используют в начинках для блинов, как ингредиент муссах, коктейлях, мороженом и молочных коктейлях, добавляют в желе и муссы, а также используют для декора выпечки, сладостей и десертов.\r\nРезаную клюкву также применяют в кашах, гарнирах, овощных и фруктовых салатах, она сочетается с гречкой, овсянкой, пшеном, рисом, творогом, йогуртом и другими кисломолочными продуктами, орехами и сухофруктами. Клюква – прекрасный ингредиент для приготовления соусов для мясных блюд.\r\nЭто отличный вариант для легкого и полезного перекуса, поэтому клюкву часто добавляют в витаминные смеси с другими сухофруктами, энергетические батончики, а также продукты быстрого приготовления (мюсли, кранчи, хлопья и др.).', 0, NULL);
