INSERT INTO `info` (`id`, `name`, `ordering`, `description`) VALUES
(1, 'for-farmer', 1, 'WANNA-FRESH.RU – фермерский агрегатор, на котором фермеры Санкт-Петербурга и Ленинградской области предоставляют пользователям возможность заказа фермерских продуктов по ценам фермерских хозяйств, без наценок, с доставкой на дом.'),
(2, 'for-farmer', 2, 'Для фермерских хозяйств WANNA-FRESH.RU – мощный инструмент увеличения продаж, без дополнительных усилий и затрат на продвижение. Размещение фермерских хозяйств на сайте бесплатное. Все цены фермерские, без наценок и переплат. Круглосуточно предоставляется большой выбор продуктов в разных фермерских хозяйствах.'),
(3, 'for-farmer', 3, 'Присутствие меню Вашего фермерского хозяйства на WANNA-FRESH.RU – это увеличение продаж, охват аудитории в сети интернет, показатель открытости Вашего фермерского хозяйства. Наличие необходимых сертификатов и лицензий – обязательное условие нашего сотрудничества с фермерскими хозяйствами.'),
(4, 'for-farmer', 4, 'Заявку на размещение можно направить на info@wanna-fresh.ru, укажите название Вашего фермерского хозяйства, почтовый адрес, сайт, координаты, e-mail и телефоны контактных лиц.'),
(5, 'for-farmer', 5, 'Благодарим Вам за проявленный интерес к сервису WANNA-FRESH.RU.'),
(7, 'terms', 1, '1. Выберите продукты в ферме. Сумма заказа должна быть не ниже минимального заказа, указанного у фермы.'),
(8, 'terms', 2, '2. Обратите внимание, что у каждого фермерского хозяйства своя минимальная сумма заказа и условия доставки'),
(9, 'terms', 3, '3. После заказа Вам позвонит представитель фермы для подтверждения заказа, даты и времени доставки. При необходимости поможет изменить заказ.'),
(10, 'terms', 4, '4. В согласованные с Вами дату и время доставки вежливый курьер фермерского хозяйства привезет Вам заказ.'),
(11, 'terms', 5, '5. Оплата при получении, оплата наличными.'),
(12, 'terms', 6, 'Фермерские хозяйства на WANNA-FRESH.RU – добросовестные труженики-фермеры, предлагающие Вам свежие натуральные продукты по собственным ценам, без наценок.');
